<style lang="scss" >
    #navMenu{
        z-index: 2;
        display: grid;align-content: space-between;justify-items: center; 
        width: auto; height: 100vh;
        position: fixed;
        padding:24px 12px;
        background-color: $main-dark-color;
        div {
            display: grid;align-content: space-between;justify-items: center; gap: 24px;
            .navMenuBtn{
                display: inline-flex; align-items: center; justify-content: center;
                height: 36px; width: 36px;
                border: none; outline: none;
                border-radius: 6px;
                background-color: transparent; 
                cursor: pointer;
                transition: .3s all;
                svg{
                    transition: .3s all;
                    path{
                        opacity: 0.5;
                        transition: .3s all;
                        fill: $second-font-color;
                    }
                }
                &:hover, &.active{svg{path{opacity: 1;}}}
                &.active{
                    background-color: $accent-color;
                    svg{
                        width: 18px; height: 18px;
                        path{fill: $main-dark-color;}
                    }
                }
            }


        } 
    }
</style>